async function gatherAndDepositWood(bot) {
  // Step 1: Find and mine 4 oak logs
  await mineBlock(bot, "oak_log", 4);

  // Step 2: Go to the chest
  const chestPosition = new Vec3(-14, -60, 2);
  await bot.pathfinder.goto(new GoalGetToBlock(chestPosition.x, chestPosition.y, chestPosition.z));

  // Step 3: Deposit the 4 oak logs into the chest
  await depositItemIntoChest(bot, chestPosition, {
    "oak_log": 4
  });
}

