async function craftAndStorePlanks(bot) {
  const chestPosition = new Vec3(-14, -60, 2);
  // Move to the chest
  await bot.pathfinder.goto(new GoalGetToBlock(chestPosition.x, chestPosition.y, chestPosition.z));
  // Open the chest and take 3 oak logs
  await getItemFromChest(bot, chestPosition, {
    "oak_log": 3
  });
  // Craft 12 oak planks
  await craftItem(bot, "oak_planks", 3);
  // Move back to the chest
  await bot.pathfinder.goto(new GoalGetToBlock(chestPosition.x, chestPosition.y, chestPosition.z));
  // Deposit the 12 oak planks into the chest
  await depositItemIntoChest(bot, chestPosition, {
    "oak_planks": 12
  });
}

