async function craftSticksAndPlaceInChest(bot) {
  const chestPosition = new Vec3(-14, -60, 2);
  const craftingTablePosition = bot.entity.position.offset(1, 0, 0);

  // Step 1: Go to the chest and take 2 oak planks
  await getItemFromChest(bot, chestPosition, {
    "oak_planks": 2
  });

  // Step 2: Place a crafting table near the bot
  await placeItem(bot, "crafting_table", craftingTablePosition);

  // Step 3: Craft 6 sticks using the crafting table
  await craftItem(bot, "stick", 3);

  // Step 4: Go back to the chest and deposit the 6 sticks
  await depositItemIntoChest(bot, chestPosition, {
    "stick": 6
  });
  bot.chat("Task completed: Crafted 6 sticks and placed them in the chest.");
}

