  const chestPosition = new Vec3(-14, -60, 2);
  await moveToChest(bot, chestPosition);

  // Step 2: Open the chest and withdraw 4 oak planks
  const itemsToGet = {
    "oak_planks": 4
  };
  await getItemFromChest(bot, chestPosition, itemsToGet);

  // Step 3: Craft a crafting table using the oak planks
  await craftItem(bot, "crafting_table", 1);

  // Step 4: Move to the position 1 block left of the chest
  const placePosition = chestPosition.offset(-1, 0, 0);
  await bot.pathfinder.goto(new GoalGetToBlock(placePosition.x, placePosition.y, placePosition.z));

  // Step 5: Place the crafting table on the ground
  await placeItem(bot, "crafting_table", placePosition);
}

