async function craftAndPlaceFurnace(bot) {
  const chestPosition = new Vec3(-17, -60, 2);
  const furnacePosition = chestPosition.offset(1, 0, 0);
  const cobblestoneNeeded = 8;
  const cobblestoneInInventory = bot.inventory.count(mcData.itemsByName.cobblestone.id);
  const cobblestoneInChest = await getItemFromChest(bot, chestPosition, {
    "cobblestone": cobblestoneNeeded
  });
  const totalCobblestone = cobblestoneInInventory + cobblestoneInChest;
  if (totalCobblestone < cobblestoneNeeded) {
    await mineBlock(bot, "cobblestone", cobblestoneNeeded - totalCobblestone);
  }
  await craftItem(bot, "furnace", 1);
  await placeItem(bot, "furnace", furnacePosition);
  const tools = {
    "wooden_pickaxe": 1,
    "stone_pickaxe": 1
  };
  await depositItemIntoChest(bot, chestPosition, tools);
}

