async function craftAndPlaceCraftingTable(bot) {
  const chestPosition = new Vec3(-14, -60, 2);
  const craftingTablePosition = chestPosition.offset(-1, 0, 0);

  // Move to the chest
  await bot.pathfinder.goto(new GoalGetToBlock(chestPosition.x, chestPosition.y, chestPosition.z));

  // Open the chest and withdraw 4 oak planks
  await getItemFromChest(bot, chestPosition, {
    "oak_planks": 4
  });

  // Craft a crafting table
  await craftItem(bot, "crafting_table", 1);

  // Move to the position 1 block left of the chest
  await bot.pathfinder.goto(new GoalGetToBlock(craftingTablePosition.x, craftingTablePosition.y, craftingTablePosition.z));

  // Place the crafting table on the ground
  await placeItem(bot, "crafting_table", craftingTablePosition);
}

