async function craftAndPlaceFurnace(bot) {
  const cobblestoneNeeded = 8;
  const cobblestoneInInventory = bot.inventory.count(mcData.itemsByName.cobblestone.id);
  let cobblestoneToCollect = cobblestoneNeeded - cobblestoneInInventory;
  if (cobblestoneToCollect > 0) {
    const chestPosition = new Vec3(-14, -60, 2);
    await getItemFromChest(bot, chestPosition, {
      "cobblestone": cobblestoneToCollect
    });
    const cobblestoneInInventoryAfterChest = bot.inventory.count(mcData.itemsByName.cobblestone.id);
    cobblestoneToCollect = cobblestoneNeeded - cobblestoneInInventoryAfterChest;
  }
  if (cobblestoneToCollect > 0) {
    await mineBlock(bot, "cobblestone", cobblestoneToCollect);
  }
  await craftItem(bot, "furnace", 1);
  const chestPosition = new Vec3(-14, -60, 2);
  const furnacePosition = chestPosition.offset(1, 0, 0);
  await placeItem(bot, "furnace", furnacePosition);
}

